//---------------------------------------------------------------------------------------
// Demographic Questions Rules - show/hide based on answers   
//---------------------------------------------------------------------------------------
// Rule Demo01 based on age show number of days, months, or years  
//----------------------------------------------------------------  
questionObject["p_age_dd"].show = false; questionObject["p_age_mm"].show = false; questionObject["p_age_yy"].show = false; questionObject["p_kg"].show = false
if (questionObject["p_age_q"].answer == "Age < month") { questionObject["p_age_dd"].show = true; questionObject["p_kg"].show = true }
else if (questionObject["p_age_q"].answer == "Age â‰¤ 1 year") { questionObject["p_age_mm"].show = true; questionObject["p_kg"].show = true }
else if (questionObject["p_age_q"].answer == "Age > 1 year") { questionObject["p_age_yy"].show = true }
// ----------
// validation rules months should not exceed 11 and days should not exceed 29, years < 100
// -------------------
questionObject["p_age_mm"].error = false; questionObject["p_age_dd"].error = false; questionObject["p_age_yy"].error = false 
if (questionObject["p_age_mm"].show == true && questionObject["p_age_mm"].answer > 11) { questionObject["p_age_mm"].error = "months should be 11 or less" }
if (questionObject["p_age_dd"].show == true && questionObject["p_age_dd"].answer > 29) { questionObject["p_age_dd"].error = "days should be 29 or less" }
if (questionObject["p_age_yy"].show == true && questionObject["p_age_yy"].answer > 100) { questionObject["p_age_yy"].error = "years should be 100 or less" }
