// 1. if blood culture is taken and its date is inside SBAP period  &&  
// 2. blood match organism in urine culture or penu samples or SSI samples 
// 3. blood isolates are inside the excluded pathogen list in each in UTI and Pneumonia 
// first bsi_is_2nd_01 period condition, 02 for matched organism, 03 for exclusion period, all should be true to conside secondary case 
//-------------------------------------------------------------------------------------------------------------------------------------
if (questionObject["bsi_infection_type"].answer != "Not met" && questionObject["infection_type"].answer != "Not met" ) 
{
    questionObject["bsi_is_2nd_01"].answer = 0; questionObject["bsi_is_2nd_02"].answer = 0; questionObject["bsi_is_2nd_03"].answer = 0
    // ABUTI Case 
    if (questionObject["infection_type"].answer == "ABUTI"){questionObject["bsi_is_2nd_01"].answer = 1; questionObject["bsi_is_2nd_02"].answer = 1; questionObject["bsi_is_2nd_03"].answer = 1}
    // "Pneumonia 2", "Pneumonia 3" 
    if (questionObject["infection_type"].answer == "Pneumonia 2" || questionObject["infection_type"].answer == "Pneumonia 3")
    {
        // scenario 2
        if  ( questionObject["pneu_blood_sample_date"].show == true && questionObject["pneu_bal_sample_date"].show == false && questionObject["pneu_endo_sample_date"].show == false && questionObject["pneu_pleu_sample_date"].show == false)
        {
            questionObject["bsi_is_2nd_01"].answer = 1; questionObject["bsi_is_2nd_02"].answer = 1; questionObject["bsi_is_2nd_03"].answer = 1
        }
        // scenario 1 
        if  ( questionObject["pneu_blood_sample_date"].show == true && (questionObject["pneu_bal_sample_date"].show == true || questionObject["pneu_endo_sample_date"].show == true || questionObject["pneu_pleu_sample_date"].show == true))
        {
            // SBAP period condition 
            if (questionObject["pneu_blood_sample_date"].answer > questionObject["sbap_from_date"].answer && questionObject["pneu_blood_sample_date"].answer < questionObject["sbap_to_date"].answer)
            {
                questionObject["bsi_is_2nd_01"].answer = 1
            }
            if  ( (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_bal_isolate_no_01"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_bal_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_bal_isolate_no_02"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_bal_path2_name"].answer)    
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_bal_isolate_no_01"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_bal_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_bal_isolate_no_02"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_bal_path2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }
            if  ( (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_endo_isolate_no_01"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_endo_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_endo_isolate_no_02"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_endo_path2_name"].answer)    
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_endo_isolate_no_01"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_endo_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_endo_isolate_no_02"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_endo_path2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }
            if  ( (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_pleu_isolate_no_01"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_pleu_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_01"].show == true && questionObject["pneu_pleu_isolate_no_02"].show == true && questionObject["pneu_blood_path1_name"].answer == questionObject["pneu_pleu_path2_name"].answer)    
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_pleu_isolate_no_01"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_pleu_path1_name"].answer)
                ||
                (questionObject["pneu_blood_isolate_no_02"].show == true && questionObject["pneu_pleu_isolate_no_02"].show == true && questionObject["pneu_blood_path2_name"].answer == questionObject["pneu_pleu_path2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }                        
            // any one not in exclusion as isolate no generated 
            if (questionObject["pneu_blood_isolate_no_01"].show == true || questionObject["pneu_blood_isolate_no_02"].show == true) 
            {
                questionObject["bsi_is_2nd_03"].answer = 1
            }
        }
    }
    // "SUTI"
    if (questionObject["infection_type"].answer == "SUTI 1b" || questionObject["infection_type"].answer == "SUTI 1a" || questionObject["infection_type"].answer == "SUTI 2")
    {
        // scenario 1 
        if  ( questionObject["uti_blood_culture_date"].show == true) 
        {
            // SBAP period condition 
            if (questionObject["uti_blood_culture_date"].answer > questionObject["sbap_from_date"].answer && questionObject["uti_blood_culture_date"].answer < questionObject["sbap_to_date"].answer)
            {
                questionObject["bsi_is_2nd_01"].answer = 1
            }
            if  ( (questionObject["uti_blood_isolate_no_01"].show == true && questionObject["uti_urine_isolate_no_01"].show == true && questionObject["uti_blood_path1_name"].answer == questionObject["uti_urine_path1_name"].answer)
                ||
                (questionObject["uti_blood_isolate_no_01"].show == true && questionObject["uti_urine_isolate_no_02"].show == true && questionObject["uti_blood_path1_name"].answer == questionObject["uti_urine_path2_name"].answer)    
                ||
                (questionObject["uti_blood_isolate_no_02"].show == true && questionObject["uti_urine_isolate_no_01"].show == true && questionObject["uti_blood_path2_name"].answer == questionObject["uti_urine_path1_name"].answer)
                ||
                (questionObject["uti_blood_isolate_no_02"].show == true && questionObject["uti_urine_isolate_no_02"].show == true && questionObject["uti_blood_path2_name"].answer == questionObject["uti_urine_path2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }
            // any one not in exclusion as isolate no generated 
            if (questionObject["uti_blood_isolate_no_01"].show == true || questionObject["uti_blood_isolate_no_02"].show == true) 
            {
                questionObject["bsi_is_2nd_03"].answer = 1
            }
        }
    }
    // "SSI"
    if (questionObject["infection_type"].answer == "Superficial Incisional SSI" || questionObject["infection_type"].answer == "Deep Incisional SSI" && questionObject["infection_type"].answer == "Organ/Space Incisional SSI")
    {
        // scenario 1 
        if  (questionObject["ssi_culture_date"].show == true)
        {
            // SBAP period condition 
            if (questionObject["bsi_culture_date"].answer > questionObject["sbap_from_date"].answer && questionObject["bsi_culture_date"].answer < questionObject["sbap_to_date"].answer)
            {
                questionObject["bsi_is_2nd_01"].answer = 1
            }
            if (questionObject["bsi_culture1_date"].answer > questionObject["sbap_from_date"].answer && questionObject["bsi_culture1_date"].answer < questionObject["sbap_to_date"].answer)
            {
                questionObject["bsi_is_2nd_01"].answer = 1
            }
            if (questionObject["bsi_culture2_date"].answer > questionObject["sbap_from_date"].answer && questionObject["bsi_culture2_date"].answer < questionObject["sbap_to_date"].answer)
            {
                questionObject["bsi_is_2nd_01"].answer = 1
            }
            if  ( (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_01"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_02"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture_pathogen2_name"].answer)    
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_01"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_02"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture_pathogen2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }
            if  ( (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_11"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture1_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_12"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture1_pathogen2_name"].answer)    
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_11"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture1_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_12"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture1_pathogen2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }
            if  ( (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_21"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture2_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_01"].show == true && questionObject["bsi_isolate_no_22"].show == true && questionObject["ssi_path1_name"].answer == questionObject["bsi_culture2_pathogen2_name"].answer)    
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_21"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture2_pathogen1_name"].answer)
                ||
                (questionObject["ssi_isolate_no_02"].show == true && questionObject["bsi_isolate_no_22"].show == true && questionObject["ssi_path2_name"].answer == questionObject["bsi_culture2_pathogen2_name"].answer)
                )          
            { 
                questionObject["bsi_is_2nd_02"].answer = 1
            }                        
            // any one not in exclusion as isolate no generated 
            if (questionObject["bsi_isolate_no_01"].show == true || questionObject["bsi_isolate_no_02"].show == true || 
                questionObject["bsi_isolate_no_11"].show == true || questionObject["bsi_isolate_no_12"].show == true ||
                questionObject["bsi_isolate_no_21"].show == true || questionObject["bsi_isolate_no_22"].show == true ) 
            {
                questionObject["bsi_is_2nd_03"].answer = 1
            }
        }
    }     
    // BSI is secondary infection if all counters = 1
    if (questionObject["bsi_is_2nd_01"].answer == 1 && questionObject["bsi_is_2nd_02"].answer == 1 && questionObject["bsi_is_2nd_03"].answer == 1) 
    {
        questionObject["bsi_infection_type"].show = true
        questionObject["bsi_infection_type"].answer = "BSI"
        questionObject["bsi_infection_device"].show = false
    } 
    else
    {
        questionObject["infection_type"].answer = questionObject["bsi_infection_type"].answer; questionObject["bsi_infection_type"].show = false
        questionObject["infection_device"].answer = questionObject["bsi_infection_device"].answer; questionObject["bsi_infection_device"].show = false
        questionObject["doi"].answer = questionObject["bsi_doi"].answer
    }  
} 
//----------------------------------------------
// infection location
//----------------------------------------------- 
questionObject["infection_location"].show = false
if (questionObject["infection_type"].answer != "Not met") 
{
    questionObject["doi_d"].answer = new Date(questionObject["doi"].answer).valueOf() / (1000 * 3600 * 24)
    questionObject["icu_d"].answer = new Date(questionObject["icu_admit_date"].answer).valueOf() / (1000 * 3600 * 24)
    questionObject["hos_d"].answer = new Date(questionObject["hos_admit_date"].answer).valueOf() / (1000 * 3600 * 24)
    questionObject["infection_location"].answer = "Present on Hospital admission"
    questionObject["infection_location"].show = true
    if ( (questionObject["doi_d"].answer - questionObject["hos_d"].answer) > 2 ) 
    {
        questionObject["infection_location"].answer = "Hospital-acquired Infection"
        questionObject["infection_location"].show = true
        if  ((questionObject["doi_d"].answer - questionObject["icu_d"].answer) > 2 )
        {
            questionObject["infection_location"].answer = "ICU-acquired Infection"
            if (questionObject["infection_type"].answer == "Superficial Incisional SSI" || questionObject["infection_type"].answer == "Deep Incisional SSI" || questionObject["infection_type"].answer == "Organ/Space Incisional SSI")
            {
                questionObject["infection_location"].answer = "SSI due to operation inside the hospital"
            }
            questionObject["infection_location"].show = true
        }
    }
}
